import logo from "./images/logo.jpeg";


const App = () => {
    const [inputList, setInputList] = useState("");
    const [listArr, setListArr] = useState([]);



    const itemEvent = (e) => {

        setInputList(e.target.value);

    };

    const itemList = (e) => {
        setListArr((oldItems) => {
            return [...oldItems, inputList];
        });
        setInputList(" ");
    };

    const deleteItems = (e) => {

        let id = e.target.id;

        console.log(id);

        setListArr((oldItems) => {
            return oldItems.filter((elem, index) => {
                console.log("deleted");
                if (index != id) {
                    return elem;
                }


            });
        });


    };

